mapper.update:
update data_batch set
batch_no =#{batchNo}, client=#{client}, area=#{area}, bail_date=#{caseTime}, case_type=#{caseType}, expect_time=#{returnTime}, target_rate=#{targetRate}, remark=#{remark},
where id = #{id}

mapper.select:
select id, batch_no as batchNo,client,area,bail_date as caseTime,case_type as caseType,expect_time as returnTime,target_rate as targetRate,remark,upload_time as uploadTime,batch_status as batchStatus,DATE_FORMAT(create_time,'%Y-%m-%d %H:%i:%S') as createTime,create_user as creatUser,real_time as realReturnTime, batch_remark as batchRemark,upload_time as uploadTime,total_amt as totalAmt,user_count as userCount from data_batch where id in
#{item}
order by upload_time asc,create_time desc
