mapper.update:
update data_batch set
batch_no =#{batchNo}, client=#{client}, area=#{area}, bail_date=#{caseTime}, case_type=#{caseType}, expect_time=#{returnTime}, target_rate=#{targetRate}, remark=#{remark},
where id = #{id}

mapper.select:
select id,

    ${reddemCode}

from data_batch

    delete_flag = 0


            AND  batch_no like CONCAT('%',#{batchNo},'%')


            and batch_no in

                #{item}



            and client in

                #{item}



            and batch_status in

                #{item}



            AND area  like CONCAT('%',#{area},'%')


            AND case_type like CONCAT('%',#{caseType},'%')



               and dbo.fnFormatDate(bail_date, 'yyyy-MM-dd ')>=  dbo.fnFormatDate(#{startTime}, 'yyyy-MM-dd ')   


               and dbo.fnFormatDate(bail_date, 'yyyy-MM-dd ')<=  dbo.fnFormatDate(#{endTime}, 'yyyy-MM-dd ')   



order by ${orderBy} ${sort}

select id,

    ${reddemCode}

from data_batch
 where  id in

        #{item}
