mapper.update:
update data_case set
money = #{money}, balance = #{balance}, status = #{status},
where id = #{id}

mapper.select:
select da.batch_no as batchNo,da.client,da.collect_area as collectArea,da.dept,da.odv,da.status,da.area,da.expect_time as expectTime,
da.collect_status as collectStatus , da.account_age as accountAge ,da.seq_no as seqNo,da.case_date as caseDate,da.collect_hand as collectHand,
da.real_repay_date as realRepayDate ,da.name as name ,da.card_no as cardo,da.ident_no as identNo,da.distribute_time as distributeTime,
da.money as money,da.balance as balance,da.over_days as overDays,da.pro_repay_date as proPepayDate,da.distribute_history as distributeHistory,
da.bank_amt as bankAmt,da.en_repay_amt as enRepayAmt,da.pro_repay_date as proRepayDate
from data_case da LEFT JOIN data_collection dc on da.id = dc.case_id

    1 = 1

        --                 委托方

            and da.client = #{client}

        --                 批次号

            and da.batch_no = #{batchNo}


            and da.odv = #{odv}


            and da.dept = #{dept}



            and da.account_age = #{accountAge}


            and da.real_return_time = #{realReturnTime}

        --                 个案序列号

            and da.seq_no = #{seqNo}


            and da.collect_area = #{collectArea}


            and da.overDays = #{overDays}


            and da.area = #{area}


            and da.license = #{license}


            and da.expect_time = #{expectTime}


            and da.collect_hand = #{collectHand}


            and da.distribute_status = #{distributeStatus}


            and da.identNo = #{identNo}


            and da.vin = #{vin}

        --                 姓名

            and da.name = #{name}

        --                 证件号

            and da.iden_no = #{idenNo}

        --                 逾期账龄

            and da.account_age = #{accountAge}

        --                 案件状态

            and da.status = #{status}


            and da.bank = #{bank}


            and da.collect_date = #{collectDate}


            and dc.collectInfo = #{collectInfo}

        --                 催收状态

            and dc.collect_status = #{collectStatus}

        --                 案件类型

            and da.case_type = #{caseType}

        --                 标记颜色

            and da.color = #{color}

        --                 卡号

            and da.card_no = #{cardNo}

        --                 档案号

            and da.archive_no = #{archiveNo}

        --                 是否新分配（0-否，1-是）

            and da.new_case = #{newCase}

        --                 还款状态

            and da.repay_status = #{repayStatus}


        --                 委案日期

               and DATE_FORMAT(da.case_date, '%Y-%m-%d')>=  DATE_FORMAT(#{caseDateStart}, '%Y-%m-%d')   


               and DATE_FORMAT(da.case_date, '%Y-%m-%d')>=  DATE_FORMAT(#{caseDateEnd}, '%Y-%m-%d')   

        --                 还款日期

               and DATE_FORMATd(da.repay_date, '%Y-%m-%d')>=  DATE_FORMAT(#{repayDateStart}, '%Y-%m-%d')   


               and DATE_FORMAT(da.repay_date, '%Y-%m-%d')>=  DATE_FORMAT(#{repayDateEnd}, '%Y-%m-%d')   

        --                 委案金额

               and DATE_FORMAT(da.money, '%Y-%m-%d')>=  DATE_FORMAT(#{moneyStart}, '%Y-%m-%d')   


               and DATE_FORMAT(da.money, '%Y-%m-%d')>=  DATE_FORMAT(#{moneyEnd}, '%Y-%m-%d')   


        --                 实际退案日

               and DATE_FORMAT(da.real_return_time, '%Y-%m-%d')>=  DATE_FORMAT(#{realRepayDateStart}, '%Y-%m-%d')   


               and DATE_FORMAT(da.real_return_time, '%Y-%m-%d')>=  DATE_FORMAT(#{realRepayDateEnd}, '%Y-%m-%d')

        select
        da.collect_status as collectStatus , da.account_age as accountAge ,da.seq_no as seqNo,da.case_date as caseDate,
        da.real_repay_date as realRepayDate ,da.name as name ,da.card_no as cardo,da.ident_no as identNo,
        da.money as money,da.balance as balance,da.over_days as overDays,da.pro_repay_date as proPepayDate,
        da.bank_amt as bankAmt,dc.collect_info as collectInfo
        from data_case da LEFT JOIN data_collection dc on da.id = dc.case_id

            1 = 1

--                 委托方

                    and da.client = #{client}

--                 批次号

                    and da.batch_no = #{batchNo}

--                 个案序列号

                    and da.seq_no = #{seqNo}

--                 姓名

                    and da.name = #{name}

 --                 证件号

                    and da.iden_no = #{idenNo}

 --                 逾期账龄

                    and da.account_age = #{accountAge}


                    and da.account = #{account}

--                 案件状态

                    and da.status = #{status}

--                 催收状态

                    and da.collect_status = #{collectStatus}

--                 案件类型

                    and da.case_type = #{caseType}

--                 标记颜色

                    and da.color = #{color}

--                 卡号

                    and da.card_no = #{cardNo}

--                 档案号

                    and da.archive_no = #{archiveNo}

--                 是否新分配（0-否，1-是）

                    and da.new_case = #{newCase}

--                 还款状态

                    and da.repay_status = #{repayStatus}

--                 减免状态

                    and da.reduce_status = #{reduceStatus}

--                 报备状态

                    and da.report_status = #{reportStatus}


                --                 委案日期

                       and DATE_FORMAT(da.case_date, '%Y-%m-%d')>=  DATE_FORMAT(#{caseDateStart}, '%Y-%m-%d')   


                       and DATE_FORMAT(da.case_date, '%Y-%m-%d')>=  DATE_FORMAT(#{caseDateEnd}, '%Y-%m-%d')   

                --                 还款日期

                       and DATE_FORMATd(da.repay_date, '%Y-%m-%d')>=  DATE_FORMAT(#{repayDateStart}, '%Y-%m-%d')   


                       and DATE_FORMAT(da.repay_date, '%Y-%m-%d')>=  DATE_FORMAT(#{repayDateEnd}, '%Y-%m-%d')   

                --                 委案金额

                       and DATE_FORMAT(da.money, '%Y-%m-%d')>=  DATE_FORMAT(#{moneyStart}, '%Y-%m-%d')   


                       and DATE_FORMAT(da.money, '%Y-%m-%d')>=  DATE_FORMAT(#{moneyEnd}, '%Y-%m-%d')   


                --                 实际退案日

                       and DATE_FORMAT(da.real_return_time, '%Y-%m-%d')>=  DATE_FORMAT(#{realRepayDateStart}, '%Y-%m-%d')   


                       and DATE_FORMAT(da.real_return_time, '%Y-%m-%d')>=  DATE_FORMAT(#{realRepayDateEnd}, '%Y-%m-%d')
