mapper.update:
update data_collection set
collect_time = #{collectTime}, result = #{result},
where id = #{id}

mapper.select:
select c.id,t.name,c.case_id as caseId,t.seq_no as seqNo,t.color,c.bail_date as bailDate,t.expect_time as expectTime,t.area, t.dept,t.odv,c.measure,c.collect_time as collectTime,c.target_name as targetName,c.relation,c.tel_type as telType, c.mobile,c.result,c.method,c.repay_time as repayTime, c.repay_amt as repayAmt,c.reduce_amt as reduceAmtexpectTime,c.collect_status collectStatus,c.reduce_status as reduceStatus, t.account_age as accountAge,t.case_date as caseDate,t.card_no as cardNo,t.ident_no as identNo, t.money,t.balance,t.bank_amt as bankAmt,c.collect_info as collectInfo,t.over_days as overDays, t.en_repay_amt as enRepayAmt,c.next_foll_date as nextFollDate,c.leave_days as leaveDays,c.new_money as moneyStart, c.last_phone_time as lastPhoneTime,c.remind,count_follow as countFollow,c.collection_type as collectionType, DATE_FORMAT(t.distribute_time, '%Y-%m-%d %H:%i') as caseAllotTime,t.status as caseStatus,c.tel_phone as telPhone from data_collection c LEFT JOIN data_case t on c.case_id = t.id
c.delete_flag = 0 and t.delete_flag = 0
AND t.dept like CONCAT('%',#{dept},'%')
AND t.collect_area = #{area}
and t.odv in
#{item}
AND c.measure like CONCAT('%',#{measure},'%')
and t.client in
#{item}
and t.batch_no in
#{item}
AND t.case_status l= #{caseStatus}
AND c.result like CONCAT('%',#{result},'%')
AND t.card_no like CONCAT('%',#{cardNo},'%')
AND c.collect_info like CONCAT('%',#{collectInfo},'%')
AND t.color like CONCAT('%',#{color},'%')
AND t.seq_no like CONCAT('%',#{seqno},'%')
AND c.target_name like CONCAT('%',#{targetName},'%')
and t.account_age = #{accountAge}
and t.ident_no = #{identNo}
and t.batch_no = #{batchNo}
and c.collect_status = #{collectStatus}
and t.case_type = #{caseType}
and t.archive_no = #{archiveNo}
and c.new_case = #{newCase}
and c.repay_status = #{repayStatus}
and c.remark = #{remark}
AND c.tel_name like CONCAT('%',#{telName},'%')
AND c.tel_phone like CONCAT('%',#{telPhone},'%')
AND c.tel_phone like CONCAT('%',#{collectPhoneAddr},'%')
and DATE_FORMAT(t.distribute_time, '%Y-%m-%d %H:%i')>= DATE_FORMAT(#{caseAllotTimeStart}, '%Y-%m-%d')
and DATE_FORMAT(t.distribute_time, '%Y-%m-%d %H:%i')<= DATE_FORMAT(#{caseAllotTimeEnd}, '%Y-%m-%d')
and DATE_FORMAT(c.next_foll_date, '%Y-%m-%d')>= DATE_FORMAT(#{nextFollDateStart}, '%Y-%m-%d')
and DATE_FORMAT(c.next_foll_date, '%Y-%m-%d')<= DATE_FORMAT(#{nextFollDateEnd}, '%Y-%m-%d')
and DATE_FORMAT(c.last_foll_date, '%Y-%m-%d')>= DATE_FORMAT(#{lastFollDateStart}, '%Y-%m-%d')
and DATE_FORMAT(c.last_foll_date, '%Y-%m-%d')<= DATE_FORMAT(#{lastFollDateEnd}, '%Y-%m-%d')
and DATE_FORMAT(c.bail_date, '%Y-%m-%d')>= DATE_FORMAT(#{bailStartDate}, '%Y-%m-%d')
and DATE_FORMAT(c.bail_date, '%Y-%m-%d')<= DATE_FORMAT(#{bailEndDate}, '%Y-%m-%d')
and DATE_FORMAT(t.expect_time, '%Y-%m-%d')>= DATE_FORMAT(#{expectTimeStart}, '%Y-%m-%d')
and DATE_FORMAT(t.expect_time, '%Y-%m-%d')<= DATE_FORMAT(#{expectTimeEnd}, '%Y-%m-%d')
and DATE_FORMAT(c.collect_time, '%Y-%m-%d')>= DATE_FORMAT(#{collectStartTime}, '%Y-%m-%d')
and DATE_FORMAT(c.collect_time, '%Y-%m-%d')<= DATE_FORMAT(#{collectEndTime}, '%Y-%m-%d')
and DATE_FORMAT(t.case_date, '%Y-%m-%d')>= DATE_FORMAT(#{caseDateStart}, '%Y-%m-%d')
and DATE_FORMAT(t.case_date, '%Y-%m-%d')<= DATE_FORMAT(#{caseDateEnd}, '%Y-%m-%d')
and DATE_FORMAT(c.repay_time, '%Y-%m-%d')>= DATE_FORMAT(#{repayTimeStart}, '%Y-%m-%d')
and DATE_FORMAT(c.repay_time, '%Y-%m-%d')<= DATE_FORMAT(#{repayTimeEnd}, '%Y-%m-%d')
and DATE_FORMAT(t.money, '%Y-%m-%d')>= DATE_FORMAT(#{moneyStart}, '%Y-%m-%d')
and DATE_FORMAT(t.money, '%Y-%m-%d')<= DATE_FORMAT(#{moneyEnd}, '%Y-%m-%d')
order by ${orderBy} ${sort}

select id,name,case_id as caseId,seqno,color,bail_date as bailDate,expect_time as expectTime,area, dept,odv,measure,collect_time as collectTime,target_name as targetName,relation,tel_type as telType, mobile,result,method,repay_time as repayTime, repay_amt as repayAmt,reduce_amt as reduceAmtexpectTime,collect_status collectStatus,reduce_status as reduceStatus, account_age as accountAge,case_date as caseDate,card_no as cardNo,ident_no as identNo, money,balance,bank_amt as bankAmt,collect_info as collectInfo,over_days as overDays, en_repay_amt as enRepayAmt,next_foll_date as nextFollDate,leave_days as leaveDays,new_money as moneyStart, last_phone_time as lastPhoneTime,remind,count_follow as countFollow,collection_type as collectionType, case_allot_time as caseAllotTime,case_status as caseStatus,tel_phone as telPhone from data_collection
delete_flag = 0
and ident_no = #{identNo}
and batch_no = #{batchNo}

select t.name,t.account,t.card_no as cardNo,t.ident_no as identNo,t.archive_no as archiveNo,t.seq_no as seqNo,t.account_age as accountAge,t.money,t.residual_principle as residualPrinciple, c.target_name as targetName,c.relation,c.tel_phone as collectPhoneAddr,c.tel_type as contractType, c.result,t.odv,c.method,c.collect_status,t.pro_repay_date as proRepayDate,t.pro_repay_amt as proRepayAmt, c.reduce_amt as reduceAmt,c.reduce_status as reduceStatus,c.collect_info as collectInfo,t.over_days as overDays,t.batch_no as batchNo, t.case_date as caseDate,t.expect_time as expectTime,c.new_money as newMoney,t.province,t.city,t.county,t.currency_type as currencyType, t.apply_order_no as applyOrderNo,t.overdue_money as overdueMoney,t.remark,c.next_foll_date as nextFollDate,t.minimum_payment as minimumPayment, t.credit_line as creditLine,c.module from data_collection c LEFT JOIN data_case t on c.case_id = t.id
c.delete_flag = 0 and t.delete_flag = 0
AND c.dept like CONCAT('%',#{dept},'%')
AND t.collect_area = #{area}
and t.odv in
#{item}
AND c.measure like CONCAT('%',#{measure},'%')
and t.client in
#{item}
AND t.status = #{caseStatus}
AND c.result like CONCAT('%',#{result},'%')
and t.batch_no in
#{item}
AND t.card_no like CONCAT('%',#{cardNo},'%')
AND c.collect_info like CONCAT('%',#{collectInfo},'%')
AND t.color like CONCAT('%',#{color},'%')
AND t.seq_no like CONCAT('%',#{seqno},'%')
AND c.target_name like CONCAT('%',#{targetName},'%')
and t.account_age = #{accountAge}
and t.ident_no = #{identNo}
and c.collect_status = #{collectStatus}
and t.case_type = #{caseType}
and t.archive_no = #{archiveNo}
and c.new_case = #{newCase}
and c.repay_status = #{repayStatus}
and c.remark = #{remark}
AND c.tel_name like CONCAT('%',#{telName},'%')
AND c.tel_phone like CONCAT('%',#{collectPhoneAddr},'%')
and DATE_FORMAT(c.next_foll_date, '%Y-%m-%d')>= DATE_FORMAT(#{nextFollDateStart}, '%Y-%m-%d')
and DATE_FORMAT(c.next_foll_date, '%Y-%m-%d')<= DATE_FORMAT(#{nextFollDateEnd}, '%Y-%m-%d')
and DATE_FORMAT(c.last_foll_date, '%Y-%m-%d')>= DATE_FORMAT(#{lastFollDateStart}, '%Y-%m-%d')
and DATE_FORMAT(c.last_foll_date, '%Y-%m-%d')<= DATE_FORMAT(#{lastFollDateEnd}, '%Y-%m-%d')
and DATE_FORMAT(c.bail_date, '%Y-%m-%d')>= DATE_FORMAT(#{bailStartDate}, '%Y-%m-%d')
and DATE_FORMAT(c.bail_date, '%Y-%m-%d')<= DATE_FORMAT(#{bailEndDate}, '%Y-%m-%d')
and DATE_FORMAT(t.expect_time, '%Y-%m-%d')>= DATE_FORMAT(#{expectTimeStart}, '%Y-%m-%d')
and DATE_FORMAT(t.expect_time, '%Y-%m-%d')<= DATE_FORMAT(#{expectTimeEnd}, '%Y-%m-%d')
and DATE_FORMAT(c.collect_time, '%Y-%m-%d')>= DATE_FORMAT(#{collectStartTime}, '%Y-%m-%d')
and DATE_FORMAT(c.collect_time, '%Y-%m-%d')<= DATE_FORMAT(#{collectEndTime}, '%Y-%m-%d')
and DATE_FORMAT(t.case_date, '%Y-%m-%d')>= DATE_FORMAT(#{caseDateStart}, '%Y-%m-%d')
and DATE_FORMAT(t.case_date, '%Y-%m-%d')<= DATE_FORMAT(#{caseDateEnd}, '%Y-%m-%d')
and DATE_FORMAT(c.repay_time, '%Y-%m-%d')>= DATE_FORMAT(#{repayTimeStart}, '%Y-%m-%d')
and DATE_FORMAT(c.repay_time, '%Y-%m-%d')<= DATE_FORMAT(#{repayTimeEnd}, '%Y-%m-%d')
and DATE_FORMAT(t.money, '%Y-%m-%d')>= DATE_FORMAT(#{moneyStart}, '%Y-%m-%d')
and DATE_FORMAT(t.money, '%Y-%m-%d')<= DATE_FORMAT(#{moneyEnd}, '%Y-%m-%d')
order by c.${orderBy} ${sort}

SELECT dc.collect_status as collectStatus,sum(dc.en_repay_amt) as paidMoney,sum(dc.money) as commisionMoney, count(DISTINCT(dc.case_id)) as sumCase from data_collection dc where dc.delete_flag = 0
and client in
#{item}
and batchNo in
#{item}
AND odv like CONCAT('%',#{odv},'%')
and account_age = #{accountAge}
GROUP BY dc.collect_status

SELECT count(DISTINCT(dc.collect_status)) as sum from data_collection dc where dc.delete_flag = 0
and client in
#{item}
and batchNo in
#{item}
AND odv like CONCAT('%',#{odv},'%')
and account_age = #{accountAge}

SELECT dc.batch_no as batchNo,sum(dc.en_repay_amt) as paidMoney,sum(dc.money) as commisionMoney, count(DISTINCT(dc.case_id)) as sumCase from data_collection dc where dc.delete_flag = 0
and client in
#{item}
and batchNo in
#{item}
and account_age = #{accountAge}
AND dept like CONCAT('%',#{dept},'%')
AND odv like CONCAT('%',#{odv},'%')
and odv in
#{item}
AND target_name like CONCAT('%',#{targetName},'%')
GROUP BY dc.batch_no

SELECT count(DISTINCT(dc.batch_no)) as sum from data_collection dc where dc.delete_flag = 0
and client in
#{item}
and batchNo in
#{item}
and account_age = #{accountAge}
AND dept like CONCAT('%',#{dept},'%')
AND odv like CONCAT('%',#{odv},'%')
and odv in
#{item}
AND target_name like CONCAT('%',#{targetName},'%')

select id,measure,collect_time as collectTime,target_name as targetName,relation,tel_phone as telPhone,collection_type as collectionType,
result,target_name as targetName,method,repay_time as repayTime,repay_amt as repayAmt,reduce_amt as reduceAmt,reduce_status as reduceStatus,odv,collect_status as collectStatus
from data_collection where case_id = #{caseId}
