mapper.select:
select t.id,t.id as caseId,t.collect_times as collectTimes,t.name as targetName, datediff( DAY, t.collect_date,GETDATE() ) AS leaveDays ,
t.distribute_status as distributeStatus, t.tel,t.remark as result,t.repay_date as repayTime,t.summary as collectInfo,t.collect_date as collectDate, t.pro_repay_amt as repayAmt,t.collect_status as collectStatus,t.reduce_status as reduceStatus,CONVERT(varchar(100), t.distribute_time, 23) as distributeTime, t.collect_times as countFollow,t.collection_type as collectionType,t.tel as telPhone,t.latest_overdue_money as moneyStart, t.seq_no as seqNo,t.color,t.expect_time as expectTime,t.area,t.dept,t.odv,t.en_repay_amt as enRepayAmt,t.name, t.account_age as accountAge,CONVERT(varchar(100), t.case_date, 23) as caseDate,t.card_no as cardNo,t.ident_no as identNo,t.next_foll_date as nextFollDate, t.money,t.balance,t.bank_amt as bankAmt,t.over_days as overDays, t.collect_date as lastPhoneTime,t.principle,t.overdue_days as overdueDays, CONVERT(varchar(100), t.distribute_time, 20) as caseAllotTime,t.status as caseStatus,CONVERT(varchar(100), t.collect_date, 20) as lastFollowTime from data_case t
t.delete_flag = 0
and t.distribute_status= #{distributeStatus}
and t.dept in
'${item}'
AND t.collection_type = #{collectionType}
AND t.collect_area = #{area}
AND t.odv = #{odv}
and t.client in
'${item}'
and t.batch_no in
'${item}'
AND t.status = #{caseStatus}
and t.card_no in
'${item}'
AND t.summary = #{collectInfo}
AND t.color = #{color}
and t.seq_no in
'${item}'
and t.name in
'${item}'
and t.overdue_days>= ${overdueDaysStart}
and t.overdue_days <= ${overdueDaysEnd}
and t.account_age = '${accountAge}'
and t.account_age in
'${item}'
and t.status in
'${item}'
and t.collect_status in
${item}
and t.case_type in
'${item}'
and t.reduce_status in
${item}
and t.report_status in
${item}
and t.color in
'${item}'
and (t.color is null or t.color in
'${item}'
)
and t.area in
'${item}'
and t.odv in
'${item}'
and t.ident_no in
'${item}'
and t.collect_status = #{collectStatus}
and t.case_type = #{caseType}
and t.archive_no in
'${item}'
and t.repay_status = #{repayStatus}
and t.remark like '%${remark}%'
AND t.tel = #{telPhone}
and t.distribute_time>= convert(datetime,'${caseAllotTimeStart}', 20)
and t.distribute_time<= convert(datetime,'${caseAllotTimeEnd}', 20)
and t.collect_date >= convert(datetime,'${lastFollDateStart}', 20)
and t.collect_date <= convert(datetime,'${lastFollDateEnd}', 20)
and convert(datetime,t.next_foll_date, 20) >= convert(datetime,'${nextFollDateStart}', 20)
and convert(datetime,t.next_foll_date, 20) <= convert(datetime,'${nextFollDateEnd}', 20)
and convert(datetime,t.expect_time, 20) >= convert(datetime,'${expectTimeStart}', 20)
and convert(datetime,t.expect_time, 20)<= convert(datetime,'${expectTimeEnd}', 20)
and t.case_date>= convert(datetime,'${caseDateStart}', 20)
and t.case_date<= convert(datetime,'${caseDateEnd}', 20)
and convert(datetime,t.repay_date, 20)>= convert(datetime,'${repayTimeStart}', 20)
and convert(datetime,t.repay_date, 20)<= convert(datetime,'${repayTimeEnd}', 20)
and t.money>= #{moneyEnd}
and t.money<= #{moneyStart}
and t.collect_times>= ${countFollowEnd}
and t.collect_times<= ${countFollowStart}
order by ${orderBy} ${sort}

