SELECT
c.collect_status as collectStatus,sum(c.en_repay_amt) as paidMoney,sum(c.money) as commisionMoney,
count(DISTINCT(dc.case_id)) as sumCase
from
data_collection dc LEFT JOIN data_case c on dc.case_id = c.id
where dc.collect_time >= convert(datetime,#{dateStart}, 20)
and dc.collect_time <= convert(datetime,#{dateEnd}, 20)

    and c.client in

        #{item}



    AND  c.collect_status = #{collectStatus}



    AND  c.odv = #{odv}


    and c.account_age = #{accountAge}


    and c.account_age in

        #{item}




    and c.collect_status in

        #{item}



GROUP BY c.collect_status

SELECT
count(DISTINCT(c.collect_status)) as sum
from
data_collection dc LEFT JOIN data_case c on dc.case_id = c.id
where

    and c.client in

        #{item}



    and c.batch_no in

        #{item}




    AND  c.odv = #{odv}


    and c.account_age = #{accountAge}

SELECT
c.batch_no as batchNo,sum(c.en_repay_amt) as paidMoney,sum(c.money) as commisionMoney,
count(DISTINCT(dc.case_id)) as sumCase
from
data_collection dc LEFT JOIN data_case c on dc.case_id = c.id
where dc.collect_time >= convert(datetime,#{dateStart}, 20)
and dc.collect_time <= convert(datetime,#{dateEnd}, 20)

    and c.client in

        #{item}



    and c.batch_no in

        #{item}




    and c.account_age = #{accountAge}


    AND  c.dept = #{dept}


    AND  c.odv = #{odv}


    and c.odv in

        #{item}



    AND  dc.target_name = #{targetName}

GROUP BY c.batch_no

