count(DISTINCT(c.batch_no)) as sum
from
data_collection dc LEFT JOIN data_case c on dc.case_id = c.id
where

    and c.client in

        #{item}



    and c.batch_no in

        #{item}




    and c.account_age = #{accountAge}


    AND  c.dept = #{dept}


    AND  c.odv = #{odv}


    and c.odv in

        #{item}



    AND dc.target_name = #{targetName}

select c.batch_no as batchNo,c.name as targetName,c.seq_no as seqno,c.client,c.account_age as accountAge, c.money,c.m_val as mVal,b.cp_money as bankAmt,b.cp_date as bankTime, b.cp_money as cpMoney, r.repay_money as repayAmt, r.repay_date as repayTime,c.en_repay_amt as enRepayAmt, c.expect_time as expectTime,r.repay_user as payName,r.repay_type as payMethod,r.confirm_user as confimName, r.confirm_time as confimTime,c.business_type as businessType from data_case c left join data_case_repay_record r on c.id = r.data_case left join data_case_bank_reconciliation b on c.id = b.data_case where c.delete_flag = 0
AND c.name = #{targetName}
AND c.dept = #{dept}
AND (c.odv = #{odv} or r.collect_user = #{odv})
and c.odv in
#{item}
and c.client in
#{item}
and c.batch_no in
#{item}
and c.account_age = #{accountAge}
and c.account_age in
#{item}
and c.odv in
#{item}
and convert(datetime,c.pro_repay_date, 20) >= convert(datetime,#{repayTimeStart}, 20)
and convert(datetime,c.pro_repay_date, 20) <= convert(datetime,#{repayTimeEnd}, 20)
and b.cp_date>= convert(datetime,#{bankTimeStart}, 20)
and b.cp_date<= convert(datetime,#{bankTimeEnd}, 20)
and r.repay_date >= convert(datetime,#{expectTimeStart}, 20)
and r.repay_date <= convert(datetime,#{expectTimeEnd}, 20)

SELECT
count(1) as sum
from data_case c
left join data_case_repay_record r on c.id = r.data_case
left join data_case_bank_reconciliation b on c.id = b.data_case
where  c.delete_flag = 0

    AND c.name = #{targetName}


    AND  c.dept = #{dept}


    AND  c.odv = #{odv}


    and c.odv in

        #{item}



    and c.client in

        #{item}



    and c.batch_no in

        #{item}




    and c.account_age = #{accountAge}



       and convert(datetime,c.pro_repay_date, 20) >= convert(datetime,#{repayTimeStart}, 20)   


       and convert(datetime,c.pro_repay_date, 20) <= convert(datetime,#{repayTimeEnd}, 20)  


       and b.cp_date>= convert(datetime,#{bankTimeStart}, 20)  


       and b.cp_date<=  convert(datetime,#{bankTimeEnd}, 20)  


       and r.repay_date >= convert(datetime,#{expectTimeStart}, 20)


       and r.repay_date <= convert(datetime,#{expectTimeEnd}, 20)

SELECT ISNULL(sum(dc.repay_money),0) as paidMoney , ISNULL(sum(r.cp_money),0) as bankAmtC, ISNULL(sum(c.pro_repay_amt),0) as repayAmtP, ISNULL(sum(dc.repay_money*c.m_val),0) as repaidAmt,ISNULL(sum(r.cp_money*c.m_val),0) as repaidBankAmt from data_case_repay_record dc LEFT JOIN data_case c on dc.data_case = c.id LEFT JOIN data_case_bank_reconciliation r on dc.data_case = r.data_case where dc.delete_flag = 0
and convert(datetime,c.pro_repay_date, 20) >= convert(datetime,#{monthStart}, 20)
and convert(datetime,c.pro_repay_date, 20) <= convert(datetime,#{monthEnd}, 20)
AND c.dept = #{dept}
AND c.odv = #{odv}
AND client = #{client}
and c.batch_no = #{batchNo}
and c.account_age = #{accountAge}
and convert(datetime,c.pro_repay_date, 20) >= convert(datetime,#{repayTimeStart}, 20)
and convert(datetime,c.pro_repay_date, 20) <= convert(datetime,#{repayTimeEnd}, 20)
and r.cp_date >= convert(datetime,#{bankTimeStart}, 20)
and r.cp_date <= convert(datetime,#{bankTimeEnd}, 20)
and dc.repay_date >= convert(datetime,#{expectTimeStart}, 20)
and dc.repay_date <= convert(datetime,#{expectTimeEnd}, 20)

select id,next_foll_date as nextFollDate,dbo.fnFormatDate(max(collect_time), 'yyyy-MM-dd HH:ii') as collectTime,case_id as caseId from data_collection where case_id in
#{item}
group by case_id,id,next_foll_date as nextFollDate order by id DESC
