mapper.update:
update data_collection set
collect_time = #{collectTime}, collect_info = #{collectInfo},
where id = #{id}

mapper.select:
select c.id,c.case_id as caseId,c.bail_date as bailDate,c.relation,c.tel_type as telType,c.target_name as targetName,c.leave_days as leaveDays,
c.mobile,c.result,c.method,c.repay_time as repayTime,c.measure,c.collect_time as collectTime,c.collect_info as collectInfo,c.next_foll_date as nextFollDate,
c.repay_amt as repayAmt,c.reduce_amt as reduceAmt,t.expect_time as expectTime,c.collect_status as collectStatus,c.reduce_status as reduceStatus,
c.remind,count_follow as countFollow,c.collection_type as collectionType,c.new_money as moneyStart,c.tel_phone as telPhone,
t.seq_no as seqNo,t.color,t.expect_time as expectTime,t.area,t.dept,t.odv,t.en_repay_amt as enRepayAmt,t.name,
t.account_age as accountAge,t.case_date as caseDate,t.card_no as cardNo,t.ident_no as identNo,
t.money,t.balance,t.bank_amt as bankAmt,t.over_days as overDays, t.collect_date as lastPhoneTime,
DATE_FORMAT(t.distribute_time, '%Y-%m-%d %H:%i') as caseAllotTime,t.status as caseStatus
from data_case t RIGHT  JOIN (select * from data_collection where delete_flag = 0) c on c.case_id = t.id

   t.delete_flag = 0


            AND  t.dept like CONCAT('%',#{dept},'%')


            AND c.collection_type = #{collectionType}


            AND t.collect_area = #{area}


            and t.odv in

                #{item}



            AND c.measure like CONCAT('%',#{measure},'%')


            and t.client in

                #{item}



            and t.batch_no in

                #{item}




            AND t.case_status l= #{caseStatus}


            AND c.result like CONCAT('%',#{result},'%')



            AND t.card_no like CONCAT('%',#{cardNo},'%')


            AND c.collect_info like CONCAT('%',#{collectInfo},'%')


            AND t.color like CONCAT('%',#{color},'%')


            AND t.seq_no like CONCAT('%',#{seqno},'%')



            AND t.name like CONCAT('%',#{name},'%')



            AND t.odv = #{odv}



            and t.account_age = #{accountAge}


            and t.ident_no = #{identNo}



            and t.batch_no = #{batchNo}



            and c.collect_status = #{collectStatus}



            and t.case_type = #{caseType}



            and t.archive_no = #{archiveNo}



            and c.new_case = #{newCase}


            and c.repay_status = #{repayStatus}


            and c.remark = #{remark}



            AND c.tel_name like CONCAT('%',#{telName},'%')



            AND c.tel_phone like CONCAT('%',#{telPhone},'%')



            AND c.tel_phone like CONCAT('%',#{collectPhoneAddr},'%')



               and DATE_FORMAT(t.distribute_time, '%Y-%m-%d')>=  DATE_FORMAT(#{caseAllotTimeStart}, '%Y-%m-%d')   


               and DATE_FORMAT(t.distribute_time, '%Y-%m-%d')<=  DATE_FORMAT(#{caseAllotTimeEnd}, '%Y-%m-%d')   



               and DATE_FORMAT(c.next_foll_date, '%Y-%m-%d')>=  DATE_FORMAT(#{nextFollDateStart}, '%Y-%m-%d')   


               and DATE_FORMAT(c.next_foll_date, '%Y-%m-%d')<=  DATE_FORMAT(#{nextFollDateEnd}, '%Y-%m-%d')   



               and DATE_FORMAT(c.last_foll_date, '%Y-%m-%d')>=  DATE_FORMAT(#{lastFollDateStart}, '%Y-%m-%d')   


               and DATE_FORMAT(c.last_foll_date, '%Y-%m-%d')<=  DATE_FORMAT(#{lastFollDateEnd}, '%Y-%m-%d')   



               and DATE_FORMAT(c.bail_date, '%Y-%m-%d')>=  DATE_FORMAT(#{bailStartDate}, '%Y-%m-%d')   


               and DATE_FORMAT(c.bail_date, '%Y-%m-%d')<=  DATE_FORMAT(#{bailEndDate}, '%Y-%m-%d')   


               and DATE_FORMAT(t.expect_time, '%Y-%m-%d')>=  DATE_FORMAT(#{expectTimeStart}, '%Y-%m-%d')   


               and DATE_FORMAT(t.expect_time, '%Y-%m-%d')<=  DATE_FORMAT(#{expectTimeEnd}, '%Y-%m-%d')   


               and DATE_FORMAT(c.collect_time, '%Y-%m-%d')>=  DATE_FORMAT(#{collectStartTime}, '%Y-%m-%d')   


               and DATE_FORMAT(c.collect_time, '%Y-%m-%d')<=  DATE_FORMAT(#{collectEndTime}, '%Y-%m-%d')   



               and DATE_FORMAT(t.case_date, '%Y-%m-%d')>=  DATE_FORMAT(#{caseDateStart}, '%Y-%m-%d')   


               and DATE_FORMAT(t.case_date, '%Y-%m-%d')<=  DATE_FORMAT(#{caseDateEnd}, '%Y-%m-%d')   



               and DATE_FORMAT(c.repay_time, '%Y-%m-%d')>=  DATE_FORMAT(#{repayTimeStart}, '%Y-%m-%d')   


               and DATE_FORMAT(c.repay_time, '%Y-%m-%d')<=  DATE_FORMAT(#{repayTimeEnd}, '%Y-%m-%d')   



           and t.money>=  #{moneyEnd}  


               and t.moneyStart<=  #{moneyStart}   


               and c.count_follow>=  #{countFollowEnd}  


               and c.count_follow<=  #{countFollowStart}   



order by ${orderBy} ${sort}

