select t1.id,t2.name,case_id as caseId,t2.seq_no as seqno,t2.color,t1.bail_date as bailDate,t2.expect_time as expectTime,t2.area, t2.dept,t2.odv,t1.measure,t1.collect_time as collectTime,t1.target_name as targetName,t1.relation,t1.tel_type as telType, t1.mobile,t1.result,t1.method,t1.repay_time as repayTime, t1.repay_amt as repayAmt,t1.reduce_amt as reduceAmtexpectTime,t1.collect_status collectStatus,t1.reduce_status as reduceStatus, t2.account_age as accountAge,t2.case_date as caseDate,t2.card_no as cardNo,t2.ident_no as identNo, t2.money,t2.balance,t2.bank_amt as bankAmt,t1.collect_info as collectInfo,t2.over_days as overDays, t2.en_repay_amt as enRepayAmt,t1.next_foll_date as nextFollDate,t1.leave_days as leaveDays,t1.new_money as moneyStart, t1.last_phone_time as lastPhoneTime,t1.remind,t1.count_follow as countFollow,t1.collection_type as collectionType, t1.case_allot_time as caseAllotTime,t1.case_status as caseStatus,t1.tel_phone as telPhone from data_collection t1 LEFT JOIN data_case t2 on t1.case_id=t2.id
t1.delete_flag = 0
and t2.ident_no = #{identNo}
and t2.batch_no = #{batchNo}

select t.name,t.account,t.card_no as cardNo,t.ident_no as identNo,t.archive_no as archiveNo,t.seq_no as seqNo,t.account_age as accountAge,t.money,t.residual_principle as residualPrinciple,
c.target_name as targetName,c.relation,c.tel_phone as collectPhoneAddr,c.tel_type as contractType,
c.result,t.odv,c.method,c.collect_status  as collectStatus,t.pro_repay_date as proRepayDate,t.pro_repay_amt as proRepayAmt,
