SELECT c.collect_status as collectStatus,sum(c.en_repay_amt) as paidMoney,sum(c.money) as commisionMoney, count(DISTINCT(dc.case_id)) as sumCase from data_collection dc LEFT JOIN data_case c on dc.case_id = c.id where dc.delete_flag = 0
and c.client in
#{item}
and c.batch_no in
#{item}
AND c.odv like CONCAT('%',#{odv},'%')
and c.account_age = #{accountAge}
GROUP BY c.collect_status

SELECT count(DISTINCT(c.collect_status)) as sum from data_collection dc LEFT JOIN data_case c on dc.case_id = c.id where dc.delete_flag = 0
and c.client in
#{item}
and c.batch_no in
#{item}
AND c.odv like CONCAT('%',#{odv},'%')
and c.account_age = #{accountAge}

SELECT c.batch_no as batchNo,sum(c.en_repay_amt) as paidMoney,sum(c.money) as commisionMoney, count(DISTINCT(dc.case_id)) as sumCase from data_collection dc LEFT JOIN data_case c on dc.case_id = c.id where dc.delete_flag = 0
and c.client in
#{item}
and c.batch_no in
#{item}
and c.account_age = #{accountAge}
AND c.dept like CONCAT('%',#{dept},'%')
AND c.odv like CONCAT('%',#{odv},'%')
and c.odv in
#{item}
AND target_name like CONCAT('%',#{targetName},'%')
GROUP BY c.batch_no

SELECT count(DISTINCT(c.batch_no)) as sum from data_collection dc LEFT JOIN data_case c on dc.case_id = c.id where dc.delete_flag = 0
and c.client in
#{item}
and c.batch_no in
#{item}
and c.account_age = #{accountAge}
AND c.dept like CONCAT('%',#{dept},'%')
AND c.odv like CONCAT('%',#{odv},'%')
and c.odv in
#{item}
AND target_name like CONCAT('%',#{targetName},'%')
