mapper.update:
update data_collection set
collect_time = #{collectTime}, collect_info = #{collectInfo},
where id = #{id}

mapper.select:
select id,dept,create_time as createTime from data_collection
delete_flag = 0
AND dept like dbo.CONCAT('%',#{dept},'%')
and dbo.fnFormatDate(create_time, 'yyyy-MM-dd')>= #{startTime}
and dbo.fnFormatDate(create_time, 'yyyy-MM-dd')<= #{endTime}

select c.id,c.case_id as caseId,c.bail_date as bailDate,c.relation,c.tel_type as telType,c.target_name as targetName,c.leave_days as leaveDays,
c.mobile,c.result,c.method,c.repay_time as repayTime,c.measure,dbo.fnFormatDate(c.collect_time, 'yyyy-MM-dd HH:mm') as collectTime,c.collect_info as collectInfo,c.next_foll_date as nextFollDate,
c.repay_amt as repayAmt,c.reduce_amt as reduceAmt,t.expect_time as expectTime,c.collect_status as collectStatus,c.reduce_status as reduceStatus,
c.remind,count_follow as countFollow,c.collection_type as collectionType,c.new_money as moneyStart,c.tel_phone as telPhone,
t.seq_no as seqNo,t.color,t.area,t.dept,t.odv,t.en_repay_amt as enRepayAmt,t.name,
t.account_age as accountAge,dbo.fnFormatDate(t.case_date, 'yyyy-MM-dd') as caseDate,t.card_no as cardNo,t.ident_no as identNo,
t.money,t.balance,t.bank_amt as bankAmt,t.over_days as overDays, t.collect_date as lastPhoneTime,
dbo.fnFormatDate(t.distribute_time, 'yyyy-MM-dd HH:mm') as caseAllotTime,t.status as caseStatus
from data_case t RIGHT  JOIN data_collection  c on c.case_id = t.id

   t.delete_flag = 0 and c.delete_flag = 0


            AND  t.dept =#{dept}


            AND c.collection_type = #{collectionType}


            AND t.collect_area = #{area}


            and t.odv in

                #{item}



            AND c.measure =#{measure}


            and t.client in

                #{item}



            and t.batch_no in

                #{item}




            AND t.case_status = #{caseStatus}


            AND c.result =#{result}



            AND t.card_no =#{cardNo}


            AND c.collect_info like dbo.CONCAT('%',#{collectInfo},'%')


            AND t.color like dbo.CONCAT('%',#{color},'%')


            AND t.seq_no like dbo.CONCAT('%',#{seqno},'%')



            AND t.name like dbo.CONCAT('%',#{name},'%')



            AND t.odv = #{odv}



            and t.account_age = #{accountAge}


            and t.ident_no = #{identNo}



            and t.batch_no = #{batchNo}



            and c.collect_status = #{collectStatus}



            and t.case_type = #{caseType}



            and t.archive_no = #{archiveNo}



            and t.distribute_status=1


            and c.repay_status = #{repayStatus}


            and c.remark = #{remark}



            AND c.tel_name =#{telName}



            AND c.tel_phone =#{telPhone}



            AND c.tel_phone =#{collectPhoneAddr}



               and dbo.fnFormatDate(t.distribute_time, 'yyyy-MM-dd')>= #{caseAllotTimeStart}  


               and dbo.fnFormatDate(t.distribute_time, 'yyyy-MM-dd')<= #{caseAllotTimeEnd}   



               and dbo.fnFormatDate(c.next_foll_date, 'yyyy-MM-dd')>=  #{nextFollDateStart}  


               and dbo.fnFormatDate(c.next_foll_date, 'yyyy-MM-dd')<=  #{nextFollDateEnd}   



               and dbo.fnFormatDate(c.last_foll_date, 'yyyy-MM-dd')>= #{lastFollDateStart}  


               and dbo.fnFormatDate(c.last_foll_date, 'yyyy-MM-dd')<=  #{lastFollDateEnd}   



               and c.bail_date >=  #{bailStartDate}   


               and c.bail_date <=  #{bailEndDate}   


               and dbo.fnFormatDate(t.expect_time, 'yyyy-MM-dd')>=  #{expectTimeStart}   


               and dbo.fnFormatDate(t.expect_time, 'yyyy-MM-dd')<=  #{expectTimeEnd}   


               and c.collect_time>=  #{collectStartTime}   


               and c.collect_time<=  #{collectEndTime}   



               and t.case_date>=  #{caseDateStart}   


               and t.case_date<=  #{caseDateEnd}   



               and dbo.fnFormatDate(c.repay_time, 'yyyy-MM-dd')>=  #{repayTimeStart}   


               and dbo.fnFormatDate(c.repay_time, 'yyyy-MM-dd')<=  #{repayTimeEnd}   



           and t.money>=  #{moneyEnd}  


               and t.moneyStart<=  #{moneyStart}   


               and c.count_follow>=  #{countFollowEnd}  


               and c.count_follow<=  #{countFollowStart}   



order by ${orderBy} ${sort}

