mapper.select:
select t.id,t.id as caseId,t.collect_times as collectTimes,t.name as targetName,t.leave_Days as leaveDays,t.distribute_status  as distributeStatus,
t.tel,t.remark as result,t.repay_date as repayTime,t.summary as collectInfo,
t.pro_repay_amt as repayAmt,t.collect_status as collectStatus,t.reduce_status as reduceStatus,dbo.fnFormatDate(t.distribute_time, 'yyyy-MM-dd') as distributeTime,
t.collect_times as countFollow,t.collection_type as collectionType,t.tel as telPhone,t.latest_overdue_money as moneyStart,
t.seq_no as seqNo,t.color,t.expect_time as expectTime,t.area,t.dept,t.odv,t.en_repay_amt as enRepayAmt,t.name,
t.account_age as accountAge,dbo.fnFormatDate(t.case_date, 'yyyy-MM-dd') as caseDate,t.card_no as cardNo,t.ident_no as identNo,t.next_foll_date as nextFollDate,
t.money,t.balance,t.bank_amt as bankAmt,t.over_days as overDays, t.collect_date as lastPhoneTime,
dbo.fnFormatDate(t.distribute_time, 'yyyy-MM-dd HH:mm') as caseAllotTime,t.status as caseStatus
from data_case t

    t.delete_flag = 0


            and t.distribute_status= #{distributeStatus}


            AND  t.dept like dbo.CONCAT('%',#{dept},'%')


            and t.dept in

                #{item}



            AND t.collection_type = #{collectionType}


            AND t.collect_area = #{area}


            AND t.odv = #{odv}


            and t.odv in

                #{item}



            and t.client in

                #{item}



            and t.batch_no in

                #{item}




            AND t.case_status l= #{caseStatus}



            AND t.card_no like dbo.CONCAT('%',#{cardNo},'%')


            AND t.summary like dbo.CONCAT('%',#{collectInfo},'%')


            AND t.color like dbo.CONCAT('%',#{color},'%')


            AND t.seq_no like dbo.CONCAT('%',#{seqno},'%')



            AND t.name like dbo.CONCAT('%',#{name},'%')



            and t.account_age = #{accountAge}


            and t.ident_no = #{identNo}



            and t.collect_status = #{collectStatus}



            and t.case_type = #{caseType}



            and t.archive_no = #{archiveNo}



            and t.repay_status = #{repayStatus}


            and t.remark = #{remark}


            AND t.tel = #{telPhone}



               and dbo.fnFormatDate(t.distribute_time, 'yyyy-MM-dd')>=  #{caseAllotTimeStart}  


               and dbo.fnFormatDate(t.distribute_time, 'yyyy-MM-dd')<= #{caseAllotTimeEnd}  


               and dbo.fnFormatDate(t.collect_date, 'yyyy-MM-dd')>=  #{lastFollDateStart}   


               and dbo.fnFormatDate(t.collect_date, 'yyyy-MM-dd')<=  #{lastFollDateEnd}  


               and dbo.fnFormatDate(t.expect_time, 'yyyy-MM-dd')>=  #{expectTimeStart}   


               and dbo.fnFormatDate(t.expect_time, 'yyyy-MM-dd')<=  #{expectTimeEnd}   


               and t.case_date>=  #{caseDateStart}   


               and t.case_date<=  #{caseDateEnd}   


               and dbo.fnFormatDate(t.repay_date, 'yyyy-MM-dd')>=  #{repayTimeStart}   


               and dbo.fnFormatDate(t.repay_date, 'yyyy-MM-dd')<=  #{repayTimeEnd}   


               and dbo.fnFormatDate(t.next_foll_date, 'yyyy-MM-dd')>=  #{nextFollDateStart}   


               and dbo.fnFormatDate(t.next_foll_date, 'yyyy-MM-dd')<=  #{nextFollDateEnd}  


               and t.money>=  #{moneyEnd}  


               and t.money<=  #{moneyStart}   


               and t.collect_times>=  #{countFollowEnd}  


               and t.collect_times<=  #{countFollowStart}   



order by ${orderBy} ${sort}

