SELECT
c.collect_status as collectStatus,sum(c.en_repay_amt) as paidMoney,sum(c.money) as commisionMoney,
count(DISTINCT(dc.case_id)) as sumCase
from
data_collection dc LEFT JOIN data_case c on dc.case_id = c.id
where dc.delete_flag = 0

    and c.client in

        #{item}



    AND  c.collect_status = #{collectStatus}



    AND  c.odv = #{odv}


    and c.account_age = #{accountAge}

GROUP BY c.collect_status

SELECT
count(DISTINCT(c.collect_status)) as sum
from
data_collection dc LEFT JOIN data_case c on dc.case_id = c.id
where dc.delete_flag = 0

    and c.client in

        #{item}



    and c.batch_no in

        #{item}




    AND  c.odv = #{odv}


    and c.account_age = #{accountAge}

SELECT
c.batch_no as batchNo,sum(c.en_repay_amt) as paidMoney,sum(c.money) as commisionMoney,
count(DISTINCT(dc.case_id)) as sumCase
from
data_collection dc LEFT JOIN data_case c on dc.case_id = c.id
where dc.delete_flag = 0

    and c.client in

        #{item}



    and c.batch_no in

        #{item}




    and c.account_age = #{accountAge}


    AND  c.dept = #{dept}


    AND  c.odv = #{odv}


    and c.odv in

        #{item}



    AND target_name = #{targetName}

GROUP BY c.batch_no

SELECT
count(DISTINCT(c.batch_no)) as sum
from
data_collection dc LEFT JOIN data_case c on dc.case_id = c.id
where  dc.delete_flag = 0

    and c.client in

        #{item}



    and c.batch_no in

        #{item}




    and c.account_age = #{accountAge}


    AND  c.dept = #{dept}


    AND  c.odv = #{odv}


    and c.odv in

        #{item}



    AND target_name = #{targetName}

select top 100 c.batch_no as batchNo,c.name as targetName,c.seq_no as seqno,c.client,c.account_age as accountAge, c.money,c.m_val as mVal,b.cp_money as bankAmt,b.cp_date as bankTime, ISNULL(b.cp_money*c.m_val,0) as repaidAmtM, ISNULL(c.en_repay_amt*c.m_val,0) as repaidBankAmtM, r.repay_money as repayAmt, r.repay_date as repayTime,c.en_repay_amt as enRepayAmt, c.expect_time as expectTime,r.repay_user as payName,r.repay_type as payMethod,r.confirm_user as confimName, r.confirm_time as confimTime,c.business_type as businessType from data_case c left join data_case_repay_record r on c.id = r.data_case left join data_case_bank_reconciliation b on c.id = b.data_case where c.delete_flag = 0
AND c.name = #{targetName}
AND c.dept = #{dept}
AND c.odv = #{odv}
and c.odv in
#{item}
and c.client in
#{item}
and c.batch_no in
#{item}
and c.account_age = #{accountAge}
and convert(datetime,c.pro_repay_date, 20) >= convert(datetime,#{repayTimeStart}, 20)
and convert(datetime,c.pro_repay_date, 20) <= convert(datetime,#{repayTimeEnd}, 20)
and r.repay_date>= convert(datetime,#{bankTimeStart}, 20)
and r.repay_date<= convert(datetime,#{bankTimeEnd}, 20)
and convert(datetime,c.expect_time, 20) >= convert(datetime,#{expectTimeStart}, 20)
and convert(datetime,c.expect_time, 20) <= convert(datetime,#{expectTimeEnd}, 20)

SELECT
count(1) as sum
from
data_collection dc LEFT JOIN data_case c on dc.case_id = c.id
where  dc.delete_flag = 0

    AND target_name = #{targetName}


    AND  c.dept = #{dept}


    AND  c.odv = #{odv}


    and c.odv in

        #{item}



    and c.client in

        #{item}



    and c.batch_no in

        #{item}




    and c.account_age = #{accountAge}



       and  convert(datetime,c.pro_repay_date, 20) >= convert(datetime,#{repayTimeStart}, 20)  


       and  convert(datetime,c.pro_repay_date, 20) <= convert(datetime,#{repayTimeEnd}, 20)  


       and convert(datetime,dc.bank_time, 20) >= convert(datetime,#{bankTimeStart}, 20) 


       and convert(datetime,dc.bank_time, 20) <= convert(datetime,#{bankTimeEnd}, 20) 


       and convert(datetime,c.expect_time, 20) >= convert(datetime,#{expectTimeStart}, 20)  


       and convert(datetime,c.expect_time, 20) <= convert(datetime,#{expectTimeEnd}, 20)

SELECT ISNULL(sum(c.en_repay_amt),0) as paidMoney , ISNULL(sum(c.bank_amt),0) as bankAmtC, ISNULL(sum(c.pro_repay_amt),0) as repayAmtP, ISNULL(sum(c.en_repay_amt*dc.m_val),0) as repaidAmt,ISNULL(sum(c.bank_amt*c.m_val),0) as repaidBankAmt from data_collection dc LEFT JOIN data_case c on dc.case_id = c.id where dc.delete_flag = 0
and convert(datetime,c.pro_repay_date, 20) >= convert(datetime,#{monthStart}, 20)
and convert(datetime,c.pro_repay_date, 20) <= convert(datetime,#{monthEnd}, 20)
AND c.dept = #{dept}
AND c.odv = #{odv}
AND client = #{client}
and c.batch_no = #{batchNo}
and c.account_age = #{accountAge}
and convert(datetime,c.pro_repay_date, 20) >= convert(datetime,#{repayTimeStart}, 20)
and convert(datetime,c.pro_repay_date, 20) <= convert(datetime,#{repayTimeEnd}, 20)
and convert(datetime,dc.bank_time, 20) >= convert(datetime,#{bankTimeStart}, 20)
and convert(datetime,dc.bank_time, 20) <= convert(datetime,#{bankTimeEnd}, 20)
and convert(datetime,c.expect_time, 20) >= convert(datetime,#{expectTimeStart}, 20)
