mapper.select:
SELECT
distinct(da.odv) as dOdv
from
data_collection dct LEFT JOIN data_case da on dct.case_id = da.id
where dct.delete_flag = 0


    and dct.collect_time>= convert(datetime,#{dateStart}, 20)


    and dct.collect_time <= convert(datetime,#{dateEnd}, 20)


    AND da.area = #{area}


    AND da.client = #{client}



        #{item}

SELECT
distinct(da.odv) as dOdv
from
data_collection dct LEFT JOIN data_case da on dct.case_id = da.id
where dct.delete_flag = 0

       and dct.collect_time>=  convert(datetime,#{monthStart}, 20)  


       and dct.collect_time<=  convert(datetime,#{monthEnd}, 20)  


    AND da.area = #{area}


    AND da.client = #{client}



        #{item}

SELECT
distinct(da.odv)
from
data_collection dct LEFT JOIN data_case da on dct.case_id = da.id
where dct.delete_flag = 0

    and dct.collect_time >= convert(datetime,#{dateStart}, 20)


    and dct.collect_time <=  convert(datetime,#{dateEnd}, 20)


    AND da.area = #{area}


    AND da.client= #{client}



        #{item}

SELECT start_time as phoneTime,name as targetName,tel as phone,type as connectionType,content,result as collectionResult, repay_time as repayTime,repay_amt as repayAmtP,odv from data_collection_tel dct where dct.delete_flag = 0
and dct.start_time >= convert(datetime,#{dateStart}, 20)
and dct.start_time <= convert(datetime,#{dateEnd}, 20)
AND odv = #{odv}
AND result = #{collectionResult}
