mapper.select:
select count(1) as countTotal
from data_collection_tel dct
where dct.delete_flag = 0

    and start_time >= #{dateStart}


    and start_time <= #{dateEnd}


    AND odv like CONCAT('%',#{odv},'%')

select count(1) as countTotal
from data_collection_tel dct
where  dct.delete_flag = 0 and end_time is not null and end_time !=''

    and start_time >= #{dateStart}


    and start_time <= #{dateEnd}



    AND odv like CONCAT('%',#{odv},'%')

SELECT count(DISTINCT(dc.case_id)) as countCasePhoneNum from data_collection_tel dct LEFT JOIN data_collection dc on dc.id = dct.collection_id where dct.delete_flag = 0
and start_time >= #{dateStart}
and start_time <= #{dateEnd}
AND dct.odv like CONCAT('%',#{odv},'%')
