mapper.select:
select

from legal

  delete_flag = 0

     and cst_name = #{cstName,jdbcType=VARCHAR}


    and owner = #{owner,jdbcType=VARCHAR}


    and  legal_status = #{legalStatus,jdbcType=INTEGER}


    and ident_no = #{legalNo,jdbcType=VARCHAR}

select
count(id) as cout
from legal

  1=1

    and cst_name = #{cstName,jdbcType=VARCHAR}


    and legal_status = #{legalStatus,jdbcType=INTEGER}


    and ident_no = #{legalNo,jdbcType=VARCHAR}

select

from legal
where  delete_flag = 0 and case_id = #{caseId}
