mapper.select:
    select * from (
    select t.id,t.case_id as caseId,d.collect_status as collectStatus,d.seq_no as seqNo,d.name,d.money as caseAmt,d.en_repay_amt as repayAmt,
         t.address,(select count(id) from letter where id=t.case_id ) as times,t.apply_context as applyContext,t.address_type as addressType,
        t.module,t.relationer,d.synergy_date as applyDate,d.odv as applyer,t.synergy_date as synergyDate,t.synergyer,t.synergy_result as synergyResult
    from letter t LEFT  JOIN data_case d on t.case_id = d.id

      t.status = #{status} and d.delete_flag=0


          AND  d.collection_area = #{collectArea}


          and d.client in

            #{item}



          and d.batch_no in

            #{item}



          AND  d.seq_no like CONCAT('%',#{seqno},'%')


          AND  t.module = #{module}


          AND  t.name like CONCAT('%',#{name},'%')


          AND  d.odv = #{applyer}


          AND  d.status  = #{caseStatus}


             and d.money>=  #{caseAmtStart}  


             and d.money <=  #{caseAmtEnd}   


             and DATE_FORMAT(d.synergy_date, '%Y-%m-%d')>=  DATE_FORMAT(#{applyDateStart}, '%Y-%m-%d')   


             and DATE_FORMAT(d.synergy_date, '%Y-%m-%d')<=  DATE_FORMAT(#{applyDateEnd}, '%Y-%m-%d')   



) s
order by ${orderBy} ${sort}

select * from (
select t.id,d.collect_status as collectStatus,d.seq_no as seqNo,t.name,d.money as caseAmt,d.en_repay_amt as repayAmt,
t.address,(select count(id) from letter where id=t.case_id ) as times,t.apply_context as applyContext,t.address_type as addressType,
t.module,t.relationer,d.synergy_date as applyDate,d.odv as applyer,t.synergy_date as synergyDate,t.synergyer,t.synergy_result as synergyResult
from letter t LEFT  JOIN data_case d on t.case_id = d.id

  t.status in (1,2)


      AND  d.collection_area = #{collectArea}


      and d.client in

        #{item}



      and d.batch_no in

        #{item}



      AND  d.seq_no like CONCAT('%',#{seqno},'%')


      AND  t.module = #{module}


      AND  t.name like CONCAT('%',#{name},'%')


      AND  d.odv = #{applyer}


      AND  d.status  = #{caseStatus}


         and d.money>=  #{caseAmtStart}  


         and d.money <=  #{caseAmtEnd}   


         and DATE_FORMAT(d.synergy_date, '%Y-%m-%d')>=  DATE_FORMAT(#{applyDateStart}, '%Y-%m-%d')   


         and DATE_FORMAT(d.synergy_date, '%Y-%m-%d')<=  DATE_FORMAT(#{applyDateEnd}, '%Y-%m-%d')   



) s
order by ${orderBy} ${sort}

