 t.synergy_date as synergyDate,t.synergyer,t.synergy_result as synergyResult,d.card_no as cardNo,d.card_type as cardType,d.ident_no as identNo,d.account,
 d.currency_type as currencyType,d.archive_no as archiveNo,d.money,d.en_repay_amt as enRepayAmt,d.principle,d.last_repay_date as lastRepayDate,d.case_date as caseDate,
 d.odv,d.credit_line as creditLine,d.home_address as homeAddress,d.home_tel_number as homeTelNumber,d.tel,d.unit_name as unitName,d.unit_address as unitAddress,
 d.unit_tel_number as unitTelNumber
 from letter t LEFT  JOIN data_case d on t.case_id = d.id

   d.delete_flag=0


       AND  d.collection_area = #{collectArea}


       and d.client in

         #{item}



       and d.batch_no in

         #{item}



       AND  d.seq_no like CONCAT('%',#{seqno},'%')


       AND  t.module = #{module}


       AND  t.name like CONCAT('%',#{name},'%')


       AND  d.odv = #{applyer}


       AND  d.status  = #{caseStatus}


          and d.money>=  #{caseAmtStart}  


          and d.money <=  #{caseAmtEnd}   


          and DATE_FORMAT(d.synergy_date, '%Y-%m-%d')>=  DATE_FORMAT(#{applyDateStart}, '%Y-%m-%d')   


          and DATE_FORMAT(d.synergy_date, '%Y-%m-%d')<=  DATE_FORMAT(#{applyDateEnd}, '%Y-%m-%d')   



 ) s
 order by ${orderBy} ${sort}

