mapper.select:
select dc.id as caseId, dc.collect_area as area,dc.client,dc.batch_no as batchNo,dc.name as targetName,dc.money as money, dc.odv,dc.reduce_status as reduceStatus, dc.collect_status as collectStatus,dc.money, dc.reduce_update_time as reduceUpdateTime, dc.en_repay_amt as enRepayAmt, dcr.complete_time as completeTime,dcr.complete_user as completeUser, dcr.audit_time as auditTime,dcr.audit_user as auditUser, dcr.apply_time as reduceReferTime,dcr.apply_time as applyTime,dcr.apply_user as applyUser, dcr.apply_status as applyStatus, dcr.reduce_type as reduceType,dcr.relation, dcr.repay_amt as repayAmt,dcr.create_time as createTime, dcr.id,dcr.reduce_valid_time as reduceValidTime,dcr.seqno as seqno,dcr.approve_repay_amt as approveRepayAmt, dcr.reduce_result as reduceResult, dcr.payer ,dcr.contact_way as contactWay,dcr.sex ,dcr.age,dcr.visit_flag as visitFlag, dcr.join_flag as joinFlag,dcr.connect_flag as connectFlag,dcr.reduce_reason as reduceReason, dcr.reduce_data as reduceData,dcr.reduce_flag as reduceFlag,dcr.file_name as fileName,dcr.file_uuid as fileUuid from data_reduce_apply dcr LEFT JOIN data_case dc on dcr.seqno = dc.seq_no where dcr.delete_flag = 0 and dcr.seqno is not null
AND dc.collect_area like CONCAT('%',#{area},'%')
AND dcr.seqno like CONCAT('%',#{seqno},'%')
AND dc.name like CONCAT('%',#{targetName},'%')
and dc.batch_no = #{batchNo}
and dc.collect_status = #{collectStatus}
AND dc.client like CONCAT('%',#{client},'%')
and dc.overdue_bill_time = #{accountAge}
AND dc.odv like CONCAT('%',#{odv},'%')
and dc.reduce_status = #{reduceStatus}
and dcr.apply_status = #{applyStatus}
and dc.id = #{caseId}
and DATE_FORMAT(dcr.complete_time, '%Y-%m-%d')>= DATE_FORMAT(#{completeTimeStart}, '%Y-%m-%d')
and DATE_FORMAT(dcr.complete_time, '%Y-%m-%d')<= DATE_FORMAT(#{completeTimeEnd}, '%Y-%m-%d')
and DATE_FORMAT(dc.create_time, '%Y-%m-%d')>= DATE_FORMAT(#{reduceReferTimeStart}, '%Y-%m-%d')
and DATE_FORMAT(dc.create_time, '%Y-%m-%d')<= DATE_FORMAT(#{reduceReferTimeEnd}, '%Y-%m-%d')
and DATE_FORMAT(dcr.reduce_valid_time, '%Y-%m-%d')>= DATE_FORMAT(#{reduceValidTimeStart}, '%Y-%m-%d')
and DATE_FORMAT(dcr.reduce_valid_time, '%Y-%m-%d')<= DATE_FORMAT(#{reduceValidTimeEnd}, '%Y-%m-%d')
order by ${orderBy} ${sort}

select

    ${reddemCode}

from
data_reduce_apply dcr LEFT JOIN data_case dc on dcr.seqno = dc.seq_no
where dcr.delete_flag = 0 and dcr.seqno is not null

    AND dc.collect_area like CONCAT('%',#{area},'%')


    AND dcr.seqno like CONCAT('%',#{seqno},'%')


    AND dc.name like CONCAT('%',#{targetName},'%')


    and dc.batch_no = #{batchNo}


    and dc.collect_status = #{collectStatus}


    AND dc.client like CONCAT('%',#{client},'%')


    and dc.overdue_bill_time = #{accountAge}


    AND dc.odv  like CONCAT('%',#{odv},'%')


    and dc.reduce_status = #{reduceStatus}



    and dcr.apply_status = #{applyStatus}



    and dc.id = #{caseId}



       and DATE_FORMAT(dcr.complete_time, '%Y-%m-%d')>=  DATE_FORMAT(#{completeTimeStart}, '%Y-%m-%d')   


       and DATE_FORMAT(dcr.complete_time, '%Y-%m-%d')<=  DATE_FORMAT(#{completeTimeEnd}, '%Y-%m-%d')   



       and DATE_FORMAT(dc.create_time, '%Y-%m-%d')>=  DATE_FORMAT(#{reduceReferTimeStart}, '%Y-%m-%d')   


       and DATE_FORMAT(dc.create_time, '%Y-%m-%d')<=  DATE_FORMAT(#{reduceReferTimeEnd}, '%Y-%m-%d')   



       and DATE_FORMAT(dcr.reduce_valid_time, '%Y-%m-%d')>=  DATE_FORMAT(#{reduceValidTimeStart}, '%Y-%m-%d')   


       and DATE_FORMAT(dcr.reduce_valid_time, '%Y-%m-%d')<=  DATE_FORMAT(#{reduceValidTimeEnd}, '%Y-%m-%d')   

order by ${orderBy} ${sort}
