mapper.update:
update sys_operation_log set
url = #{url}, user_ip = #{userIp}, user_browser = #{userBrowser}, request_token = #{requestToken}, user_id = #{userId}, user_login_name = #{userLoginName}, request_body = #{requestBody}, response_body = #{responseBody},
where id = #{id}

mapper.select:
select

from sys_operation_log
where delete_flag = '0'

    and url = #{url}



        #{item}



    and user_id = #{userId}



        #{item}



    and create_time >= #{createTimeBegin}


    and create_time <= #{createTimeEnd}

order by create_time desc
