mapper.update:
update sys_organization set
org_name = #{orgName}, parent_id = #{parent.id}, org_nature = #{orgNature}, legal_person = #{legalPerson}, user_num = #{userNum}, org_address = #{orgAddress}, main_industry = #{mainIndustry}, sort = #{sort}, remark = #{remark}, update_user = #{updateUser.id},
where id = #{id}

mapper.select:
select top 1

from sys_organization
where delete_flag = '0' and org_name = #{name}
