mapper.select:
select su.id , su.mobile,su.number,su.sex,su.office_phone as officePhone, su.status,su.enable,su.username as userName,so.org_name department, su.create_time as createTime,su.create_user as 'createUser.id', su.update_user as 'updateUser.id',su.update_time as updateTime, su.delete_flag as deleteFlag, sr.role_name as role, su.leave_time as leaveTime, su.join_time as joinTime, su.department as departId, sr.id as roleId, su.position from sys_user su LEFT JOIN sys_user_role sur on su.id = sur.user_id LEFT JOIN sys_role sr on sur.role_id = sr.id LEFT JOIN sys_organization so on su.department = so.id where su.delete_flag = 0
AND su.status = #{status,jdbcType=INTEGER}
AND su.department = #{department}
AND su.number like CONCAT('%',#{number},'%')
AND su.username like CONCAT('%',#{userName},'%')
order by id desc

mapper.update:
update sys_user
set password = #{password}
where id = #{id}
