and office_phone = #{officePhone}
and callcenterid = #{callcenterid}
and id != #{id}

select

from sys_user
where  id = #{id}

select count(1) as sum from sys_user where delete_flag = 0
AND status = #{status,jdbcType=INTEGER}
AND department = #{department}
AND number like dbo.CONCAT('%',#{number},'%')
AND username like dbo.CONCAT('%',#{userName},'%')
AND login_name like dbo.CONCAT('%',#{loginName},'%')

select su.id , su.mobile,su.number,su.sex,su.office_phone as officePhone, su.status,su.enable,su.username as userName, su.create_time as createTime,su.create_user as [createUser.id], su.update_user as [updateUser.id],su.update_time as updateTime, su.delete_flag as deleteFlag, sr.role_name as role, su.leave_time as leaveTime from sys_user su LEFT JOIN sys_user_role sur on su.id = sur.user_id LEFT JOIN sys_role sr on sur.role_id = sr.id where su.delete_flag = 0 and su.status=1
AND sr.role_name = #{role}
AND username like dbo.CONCAT('%',#{username},'%')

